import React, {useState, useEffect} from 'react';
import ReportForm from './ReportForm';
import axios from 'axios';
import MovieDB from '../data/db';
import {compareData} from '../data/moviePayload';
import {withStyles, Button} from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import styles from './report.styles';


const Report = (props:any) => {
    const initValue = [
        {}
    ];

    const API_TITLE_ENPOINT = "http://www.omdbapi.com/?apikey=e60c4b4a&&t=";
    const API_ID_ENDPOINT = "http://www.omdbapi.com/?apikey=e60c4b4a&&i=";
    const [apiData, setApiData] = useState(initValue); // Defines the data pulled from IMDB through the batch given
    const [payloadData, setPayloadData] = useState(); // Defines the data given through Amazon Payload
    const [processLog, setProcessLog] = useState();
    const {classes} = props;
    const {batch} = props;

    const fetchMovieByTitle = async (batch:any) => {

    }

    const fetchMovieById = async (batch:any, endpoint:string) => {
        let response: any[] = [];
        batch.map(async (title:any) => {
            console.log('Movie title being searched: ', title.title);
            console.log('Our initial value is: ', apiData);
            axios.get(`${endpoint}+${title.title}`).then(response => {
                setApiData([...apiData, response.data]);
                console.log("Finished loading", response.data);
                processDataComparison(response.data, MovieDB)
            }); // Needs to be changed to acommodate for array in apiData
        })

        // return movies.data;
    }

    const processDataComparison = (batch:any, payload:any) => {
        const results = compareData(payload, batch);
        console.log("These are the results from comparison: ", results);

        return results;
    }

    const fetchMovieFromPayload = (title:string)=> {

    }

    const pushToState = (prevState:any, newItem:any) => {

    } 

    const manageDisplay = () => {
        return(
            <>
                <Grid container alignItems="center">
                    <Grid item lg={6}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell align="left">Movie Title</TableCell>
                                    <TableCell align="left">Positive Matches</TableCell>
                                    <TableCell align="left">Mismatches</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {apiData.map((listing: any) => (
                                    <TableRow key={listing.title}>
                                        <TableCell align="left">{listing.title}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </Grid>
                </Grid>

            </>
        );

    }


    useEffect(() => {
        fetchMovieById(batch, API_TITLE_ENPOINT);
        // manageDisplay();
        setPayloadData(MovieDB);
    }, [])


    return(
        <div>
            <h1 className={classes.headerLabel}>Data Processing</h1>
            <h3>Movie from IMDB: </h3>
            <p>{JSON.stringify(apiData)}</p>
            <br></br>
            <br></br>
            <h3>Movie from Payload:</h3>
            <p>{JSON.stringify(MovieDB)}</p>
            <br></br>
            <br></br>
            <h3>Comparing Data:</h3>
            {manageDisplay()}
            <Button onClick={() => setProcessLog(processDataComparison(apiData, MovieDB))}>Compare</Button>
        </div>
    );
}

export default withStyles(styles)(Report);
